import React from 'react';

const TaskModal = ({ task, completeTask, postponeTask, onClose }) => {
  return (
    <div className="modal">
      <div className="modal-content">
        <h2>{task.title}</h2>
        <p>{task.description}</p>
        <p>Fecha de vencimiento: {new Date(task.endDate).toLocaleString()}</p>
        <button onClick={() => completeTask(task.id)}>Completar</button>
        <button onClick={() => postponeTask(task.id)}>Posponer</button>
        <button onClick={onClose}>Cerrar</button>
      </div>
    </div>
  );
};

export default TaskModal;
