import React, { useState } from 'react';
import TaskModal from './TaskModal';

const App = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: 'Tarea 1',
      description: 'Descripción de la tarea 1',
      date: new Date(), // Asegurarse de que esto sea un objeto Date
    },
    {
      id: 2,
      title: 'Tarea 2',
      description: 'Descripción de la tarea 2',
      date: new Date(),
    },
  ]);

  const [selectedTask, setSelectedTask] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenModal = (task) => {
    setSelectedTask(task);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedTask(null);
  };

  const handlePostponeTask = () => {
    // Lógica para posponer la tarea
    const newTasks = tasks.map((task) =>
      task.id === selectedTask.id
        ? { ...task, date: new Date(task.date.getTime() + 24 * 60 * 60 * 1000) } // Añadir un día
        : task
    );
    setTasks(newTasks);
    handleCloseModal();
  };

  return (
    <div>
      <h1>Lista de Tareas</h1>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.title} - {task.date.toLocaleString()}
            <button onClick={() => handleOpenModal(task)}>Ver</button>
          </li>
        ))}
      </ul>

      {selectedTask && (
        <TaskModal
          isOpen={isModalOpen}
          task={selectedTask}
          onClose={handleCloseModal}
          onPostpone={handlePostponeTask}
        />
      )}
    </div>
  );
};

export default App;
